mapper.insert:

	SELECT NVL(MAX(club_number), 0) + 1
	FROM club

INSERT INTO club
		(
			club_number

			, profile_nickname


			, club_name


			, club_category


			, club_type


			, club_info


			, major_location


			, sub_location


			, club_public

			, singup_question1
			, singup_question2
			, singup_question3
			, club_img
			, club_img_path
			, club_total
		)
VALUES
		(
			#{clubNumber}

			, #{profileNickname}


			, #{clubName}


			, #{clubCategory}


			, #{clubType}


			, #{clubInfo}


			, #{majorLocation}


			, #{subLocation}


			, #{clubPublic}

			, #{singupQuestion1}
			, #{singupQuestion2}
			, #{singupQuestion3}
			, #{clubImg}
			, #{clubImgPath}
			, #{clubTotal}
		)

mapper.select:
SELECT count(*) 
FROM club
WHERE profile_nickname = #{profileNickname}

SELECT count(*) 
FROM club
WHERE club_name = #{clubName}
