mapper.select:
SELECT cs_number, cs_title, cs_contents, cs_writer, cs_date, cs_update, cs_img_name
FROM cs
where cs_number = ${csNumber}

mapper.insert:

	SELECT NVL(MAX(cs_number),0)+1
	FROM cs


	INSERT INTO cs
		(  cs_number
			, cs_title
			, cs_contents
			, cs_writer
			, cs_date
			,cs_img_name
		)
		
		VALUES
		(
			#{csNumber}
			, #{csTitle}
			, #{csContents}
			, #{csWriter}
			, sysdate
			, #{csImgName}
		)
