mapper.select:
SELECT p.point_id, p.point_name, p.point_content, p.point_item_type, p.point_state, p.point_view, p.point_regdate, p.point_endterm, p.point_img_name, p.point_img_path,
po.point_opt_id, po.point_period, po.point_price,
e.emoji_id, e.emoji_img_name, e.emoji_img_path FROM point p JOIN point_option po ON p.point_id = po.point_id JOIN emoji e ON po.point_id = e.point_id WHERE p.point_id = #{pointId}

mapper.update:
UPDATE point

	
		point_name = #{pointName},
	
	
		point_content = #{pointContent},
	
	
		point_regdate = #{pointRegdate}
	
	
		point_endterm = #{pointEndterm}
	
	
		point_img_name = #{pointImgName}
	
	
		point_img_path = #{pointImgPath}
	

WHERE point_id = #{pointId}
